-- ----------------------------
-- Table structure for `our_app`
-- ----------------------------
DROP TABLE IF EXISTS `our_app`;
CREATE TABLE `our_app` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `keyword` varchar(100) NOT NULL DEFAULT '',
  `price` decimal(10,0) NOT NULL DEFAULT '0',
  `icon` varchar(100) NOT NULL DEFAULT '' COMMENT '图标',
  `total` int(11) NOT NULL DEFAULT '0' COMMENT '数量',
  `type` tinyint(2) NOT NULL DEFAULT '1' COMMENT '1软件，2游戏，3付费',
  `add_time` datetime NOT NULL COMMENT '添加时间',
  `update_time` datetime NOT NULL COMMENT '更新时间',
  `user_id` int(11) NOT NULL COMMENT '发布人ID',
  `position` smallint(5) NOT NULL DEFAULT '0' COMMENT '位置',
  `remark` varchar(255) NOT NULL DEFAULT '' COMMENT '标记说明',
  `start_time` datetime DEFAULT NULL,
  `end_time` datetime DEFAULT NULL COMMENT '开始时间',
  `s_time` time DEFAULT NULL,
  `date_total` int(11) DEFAULT NULL,
  `time_type` tinyint(1) NOT NULL DEFAULT '1' COMMENT '时间类型：1全天，2时间段',
  PRIMARY KEY (`id`),
  KEY `nums` (`total`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of our_app
-- ----------------------------

-- ----------------------------
-- Table structure for `our_article`
-- ----------------------------
DROP TABLE IF EXISTS `our_article`;
CREATE TABLE `our_article` (
  `article_id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `cat_id` smallint(5) NOT NULL DEFAULT '0' COMMENT '类别ID',
  `title` varchar(150) NOT NULL DEFAULT '' COMMENT '文章标题',
  `content` longtext NOT NULL COMMENT '文章内容',
  `author` varchar(30) DEFAULT '' COMMENT '作者',
  `author_email` varchar(60) DEFAULT '' COMMENT '作者邮箱',
  `keywords` varchar(255) DEFAULT '' COMMENT '文章关键字',
  `article_type` tinyint(1) unsigned DEFAULT '2' COMMENT '文章类型：2',
  `is_open` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '是否显示：0不显示，1显示',
  `add_time` int(10) unsigned NOT NULL DEFAULT '0',
  `file_url` varchar(255) DEFAULT '' COMMENT '文章图片',
  `open_type` tinyint(1) unsigned DEFAULT '0',
  `link` varchar(255) DEFAULT '' COMMENT '外部链接',
  `description` varchar(255) DEFAULT NULL COMMENT '文章简述',
  `sort_order` int(10) DEFAULT '0' COMMENT '排序',
  `update_time` int(10) DEFAULT '0' COMMENT '修改时间',
  PRIMARY KEY (`article_id`),
  KEY `cat_id` (`cat_id`)
) ENGINE=MyISAM AUTO_INCREMENT=137 DEFAULT CHARSET=utf8;

