-- ----------------------------
-- Table structure for `our_category`
-- ----------------------------
DROP TABLE IF EXISTS `our_category`;
CREATE TABLE `our_category` (
  `cat_id` smallint(5) unsigned NOT NULL AUTO_INCREMENT,
  `cat_name` varchar(90) NOT NULL DEFAULT '' COMMENT '类别名称',
  `keywords` varchar(255) DEFAULT '' COMMENT '类别关键字',
  `cat_desc` varchar(255) DEFAULT '' COMMENT '类别简述',
  `cat_content` longtext COMMENT '类别内容',
  `parent_id` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '父类别',
  `cat_icon` varchar(50) DEFAULT '' COMMENT '类别图标',
  `cat_pic` varchar(50) DEFAULT '' COMMENT '类别图片',
  `show_in_nav` tinyint(1) NOT NULL DEFAULT '1' COMMENT '导航条显示:1显示,0不显示',
  `is_on` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '是否上架:1上架,0下架',
  `sort_order` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '类别排序',
  `add_time` int(10) NOT NULL DEFAULT '0' COMMENT '添加时间',
  `update_time` int(10) NOT NULL DEFAULT '0' COMMENT '修改时间',
  PRIMARY KEY (`cat_id`),
  KEY `parent_id` (`parent_id`)
) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of our_category
-- ----------------------------

-- ----------------------------
-- Table structure for `our_config`
-- ----------------------------
DROP TABLE IF EXISTS `our_config`;
CREATE TABLE `our_config` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '配置ID',
  `name` varchar(30) NOT NULL DEFAULT '' COMMENT '配置名称',
  `type` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '配置类型',
  `title` varchar(50) NOT NULL DEFAULT '' COMMENT '配置说明',
  `group` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '配置分组',
  `extra` varchar(255) NOT NULL DEFAULT '' COMMENT '配置值',
  `remark` varchar(100) NOT NULL COMMENT '配置说明',
  `create_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '创建时间',
  `update_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  `status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '状态',
  `value` text NOT NULL COMMENT '配置值',
  `sort` smallint(3) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  PRIMARY KEY (`id`),
  UNIQUE KEY `uk_name` (`name`),
  KEY `type` (`type`),
  KEY `group` (`group`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of our_config
-- ----------------------------

-- ----------------------------
-- Table structure for `our_datasplit`
-- ----------------------------
DROP TABLE IF EXISTS `our_datasplit`;
CREATE TABLE `our_datasplit` (
  `ds_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '分表分库ID',
  `ds_name` varchar(30) NOT NULL DEFAULT '' COMMENT '分表分库名称【总表总库名称】',
  `ds_table_database` tinyint(1) NOT NULL DEFAULT '0' COMMENT '类型:0.分表,1.分库',
  `ds_type_hash` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '分表方式hash:0.否，1.是',
  `ds_type_date` tinyint(1) NOT NULL DEFAULT '0' COMMENT '分表方式日期：0.否,1.日期【日】,2.日期【周】,3.日期【月】,4.日期【年】',
  `ds_status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '状态:0.未启用，1.启用',
  `ds_num` int(10) NOT NULL DEFAULT '0' COMMENT 'hash分表/分库数量',
  `ds_field_hash` varchar(100) NOT NULL DEFAULT '0' COMMENT 'hash分表字段',
  `ds_field_date` varchar(100) NOT NULL DEFAULT '0' COMMENT '分表字段：日期',
  `ds_remark` varchar(100) NOT NULL DEFAULT '' COMMENT '说明',
  `create_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '创建时间',
  `update_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  `sort_order` smallint(3) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `ds_editnum` int(4) NOT NULL DEFAULT '0' COMMENT '执行分表次数',
  `child_name` varchar(100) DEFAULT '' COMMENT '子表名称',
  `child_id_field` varchar(100) DEFAULT '' COMMENT '子表关联id名',
  `child_id` varchar(100) DEFAULT '' COMMENT '子表2自增ID',
  `child_name2` varchar(100) DEFAULT '' COMMENT '子表2名称',
  `child_id_field2` varchar(100) DEFAULT '' COMMENT '子表2关联id',
  `child_id2` varchar(100) DEFAULT '' COMMENT '子表2自增ID',
  PRIMARY KEY (`ds_id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of our_datasplit
-- ----------------------------

-- ----------------------------
-- Table structure for `our_datasplit_detail`
-- ----------------------------
DROP TABLE IF EXISTS `our_datasplit_detail`;
CREATE TABLE `our_datasplit_detail` (
  `ds_d_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '分表分库详情表ID',
  `ds_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '分表分库主表ID',
  `name` varchar(500) NOT NULL DEFAULT '' COMMENT '分表名/分库名',
  `is_hash_date` tinyint(4) NOT NULL DEFAULT '0' COMMENT '分表方式：0.hash,1.日期',
  `create_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '创建时间',
  `update_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  `ds_status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '状态:0.未启用，1.已启用，2.已作废1次，3.已作废2次，N.已作废N-1次',
  `begin_time` int(10) NOT NULL DEFAULT '0' COMMENT '日期分表：开始时间',
  `end_time` int(10) NOT NULL DEFAULT '0' COMMENT '日期分表，结束时间',
  `child_name` varchar(500) DEFAULT '' COMMENT '子表名称',
  `child_name2` varchar(500) DEFAULT '' COMMENT '子表2名称',
  PRIMARY KEY (`ds_d_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of our_datasplit_detail
-- ----------------------------

-- ----------------------------
-- Table structure for `our_goods`
-- ----------------------------
DROP TABLE IF EXISTS `our_goods`;
CREATE TABLE `our_goods` (
  `goods_id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `cat_id` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '类别ID',
  `goods_sn` varchar(60) NOT NULL DEFAULT '' COMMENT '商品编号',
  `goods_name` varchar(120) NOT NULL DEFAULT '' COMMENT '商品名称',
  `brand_id` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '品牌ID',
  `keywords` varchar(255) NOT NULL DEFAULT '' COMMENT '商品关键字',
  `goods_brief` varchar(255) NOT NULL DEFAULT '' COMMENT '商品概要',
  `add_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '添加时间',
  `update_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '修改时间',
  `goods_content` text NOT NULL COMMENT '商品内容详情',
  `goods_content_wap` text NOT NULL COMMENT '商品手机端内容详情',
  `goods_thumb` varchar(255) NOT NULL DEFAULT '' COMMENT '商品缩略图',
  `goods_img` varchar(255) NOT NULL DEFAULT '' COMMENT '商品轮播图图',
  `original_img` varchar(255) NOT NULL DEFAULT '' COMMENT '商品原图',
  `market_price` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '商品市场价(零售价)',
  `shop_price` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '商品销售价',
  `promote_price` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '商品促销价',
  `promote_start_date` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '促销开始日期',
  `promote_end_date` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '促销结束日期',
  `goods_number` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '商品库存数量',
  `warn_number` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '商品库存预警数',
  `is_real` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '真实/虚拟商品：1.真实,0.虚拟',
  `extension_code` varchar(30) NOT NULL DEFAULT '' COMMENT '商品扩展码',
  `is_on_sale` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '是否销售：1.上架，0.下架',
  `is_shipping` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '商品是否包邮：1.包邮，0.不包邮',
  `integral` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '商品积分',
  `is_delete` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否删除',
  `is_best` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否精品：1.是，0.否',
  `is_new` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '是否新品：1.是，0.否',
  `is_hot` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否热销：1.是，0.否',
  `bonus_type_id` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '关联优惠活动id',
  `goods_weight` decimal(10,3) unsigned NOT NULL DEFAULT '0.000' COMMENT '商品重量',
  `goods_type` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '商品属性',
  `click_count` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '商品点击量',
  `seller_note` varchar(255) NOT NULL DEFAULT '' COMMENT '商家备注',
  `suppliers_id` smallint(5) unsigned DEFAULT NULL COMMENT '供应商ID',
  `sort_order` smallint(4) unsigned NOT NULL DEFAULT '100' COMMENT '商品排序',
  PRIMARY KEY (`goods_id`),
  KEY `goods_sn` (`goods_sn`),
  KEY `cat_id` (`cat_id`),
  KEY `update_time` (`update_time`),
  KEY `brand_id` (`brand_id`),
  KEY `goods_weight` (`goods_weight`),
  KEY `promote_end_date` (`promote_end_date`),
  KEY `promote_start_date` (`promote_start_date`),
  KEY `goods_number` (`goods_number`),
  KEY `sort_order` (`sort_order`)
) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of our_goods
-- ----------------------------

-- ----------------------------
-- Table structure for `our_members`
-- ----------------------------
DROP TABLE IF EXISTS `our_members`;
CREATE TABLE `our_members` (
  `member_id` int(11) NOT NULL AUTO_INCREMENT,
  `username` varchar(50) DEFAULT NULL COMMENT '用户名',
  `password` varchar(80) DEFAULT NULL COMMENT '登录密码',
  `salt` char(6) DEFAULT NULL COMMENT '加密密钥',
  `phone` char(15) DEFAULT NULL,
  `email` varchar(255) DEFAULT NULL COMMENT '邮箱',
  `name` varchar(30) DEFAULT NULL COMMENT '用户名',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态：1 正常；0 禁用；2 其他',
  `register_ip` varchar(100) DEFAULT '000.000.000.000' COMMENT '注册IP地址',
  `register_time` int(10) DEFAULT '0' COMMENT '注册时间',
  PRIMARY KEY (`member_id`),
  KEY `member_id` (`member_id`),
  KEY `username` (`username`),
  KEY `status` (`status`)
) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8mb4 COMMENT='用户表';

-- ----------------------------
-- Records of our_members
-- ----------------------------

-- ----------------------------
-- Table structure for `our_menu`
-- ----------------------------
DROP TABLE IF EXISTS `our_menu`;
CREATE TABLE `our_menu` (
  `id` smallint(6) unsigned NOT NULL AUTO_INCREMENT,
  `name` char(40) NOT NULL DEFAULT '',
  `parentid` smallint(6) DEFAULT '0',
  `icon` varchar(20) NOT NULL DEFAULT '',
  `c` varchar(20) DEFAULT NULL,
  `a` varchar(20) DEFAULT NULL,
  `data` varchar(50) NOT NULL DEFAULT '',
  `tip` varchar(255) NOT NULL DEFAULT '' COMMENT '提示',
  `group` varchar(50) DEFAULT '' COMMENT '分组',
  `listorder` smallint(6) unsigned NOT NULL DEFAULT '999',
  `display` tinyint(1) NOT NULL DEFAULT '1' COMMENT '是否显示(1:显示,2:不显示)',
  `updatetime` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `listorder` (`listorder`),
  KEY `parentid` (`parentid`)
) ENGINE=InnoDB AUTO_INCREMENT=62 DEFAULT CHARSET=utf8;

